import React, { Component } from 'react'
import MainCard from './partbody/MainCard'
import TopArticle from './partbody/TopArticle';
import Account from './Member/Account';
import Sugestion from './partbody/Suggestion';
import axios from 'axios';
import classnames from 'classnames'
import { Container, Row, Col} from 'reactstrap';





export default class AppBody extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeTab: '1',
      products: [],
      title: '',
      categories: '',
      content: '',
      pictures: []
    };
  }
  render() {
    const looping = this.state.products.map((prod, index) => {
        return(
          <Col md='4' key={index}>
          <MainCard
            image={prod.categories}
            title={prod.title}
            harga={prod.content}
            deleteId= {(e) => this.handleDelete(prod.id)}
            buttonName='delete'
            color='primary'
          />
        </Col>
        )}
      );
    return (
      <div style={{backgroundColor:'#ececec', width:'auto', height:'auto'}} > 

				<Container >
        <Row>
        <Col xs={12} md={8}
           style={{}}   className={classnames({ active: this.state.activeTab === '2' })}
           onClick={() => { this.toggle('2'); }}
          >	

          <MainCard /> 

          </Col>

          <Col xs={6} md={4}
          style={{}}   
          > <Account/>
		<TopArticle/>
	<Sugestion/>
					</Col>

        </Row>
          <Row tabId="2" className='d-flex'>
            {looping}
          </Row>
      </Container>

      </div>
    )
  }
}
