import React, { Component } from 'react'
import ReactHtml from 'raw-html-react';
import { Card, CardImg, CardBody, Row, CardTitle, CardSubtitle, Form, FormGroup, Button, Input, Media} from 'reactstrap';
import CardComment from './CardComment';
import axios from 'axios';
import PropTypes from 'prop-types';
import CKEditor from 'ckeditor4-react';
import CreateArticle from '../Page/CreateArticle';

class EditorPreview extends Component {
  render() {
      return (
          <div className="editor-preview">
              <h2>Rendered content:</h2>
              <div dangerouslySetInnerHTML={ { __html: this.props.categories } }></div>

              <CardBody>
      <CardSubtitle style={{color:'blue', marginTop:'30px'}}>View Comment</CardSubtitle>
      </CardBody>
          </div>
      );



  }
}
EditorPreview.defaultProps = {
  categories: ''
};

EditorPreview.propTypes = {
  categories: PropTypes.string
};


class MainCard extends Component {

  render() { 
    return (
      <div>
        <Card  class="p-2 w-100 bd-highlight"
        style={{  marginTop:'40px', width:'725px', height:'auto', borederRadius:'7px', boxShadow:'0 4px 4px 0 rgba(0,0,0,0.1)' }}> 
 <CardBody>
        <CardSubtitle>   
        <Media>
      <Media><CardImg style={{width:'60px', height:'60px', marginRight:'40px', borderRadius:'50%'}} src="
      https://scontent.fcgk8-2.fna.fbcdn.net/v/t1.0-9/37228699_10156511912949419_5427734426052198400_n.jpg?_nc_cat=106&_nc_eui2=AeFbW0UNjyUU6wUYr2IRN9OeP8hreReeKw5JhLn4zpkqOk1EoX7p-oyHW8DTrHt5IrPnY0tK6xlY0Q6fIY6iv9XgAzUKHS8I46Du69aZ0qCGvQ&_nc_ht=scontent.fcgk8-2.fna&oh=c180ed12f4d6e241827c9bed4879df13&oe=5D034823
      " className="Icon"
      />
        <Media  />
      </Media>
      <Media body>
        <Media heading className="MediaHeading" style={{height:'33px', fontSize:'28px', marginBottom:'6px' }} >
         Nama
        </Media>
        <small className="text-muted">Last updated 3 mins ago</small>
         </Media>


<Button className="float-right"
            style={{width: '120px', padding:'10px', height: '35px', marginTop:'30px', borderRadius: '99px',backgroundColor: '#2e4ffc', fontFamily: 'Roboto', fontSize: '16px', fontWeight: '500', fontStyle: 'normal', fontStretch: 'normal', lineHeight: 'normal', letterSpacing: 'normal', color: '#ffffff'}}
            >+ Follow </Button>
    </Media>
        </CardSubtitle>
        </CardBody>

          <CardTitle> <CardImg style={{height:'375px', width:'725px'}} 
          src={this.props.image}
          /> 
          </CardTitle>


        <CardBody>
            {/* <CardTitle style={{height:'24px', marginBottom:'15px', marginTop:'15px', color:'#626262', fontSize:'20px'}}>
            </CardTitle> */}



        <CardSubtitle style={{height:'66px', fontSize:'28px', color:'#2e2e2e' }}>
        <h2>{this.props.harga}
          </h2>
          </CardSubtitle>

        </CardBody>
        <hr width="100%"></hr>
        <CardBody>
        <CardSubtitle>
            <Form>
            <FormGroup>
          <Input valid style={{backgroundColor:'#ededed', color:'#626262',  height:'50px', width:'665px', borderRadius:'30px'}} placeholder="Add Comment..."/>
           </FormGroup>
        </Form>

        </CardSubtitle>

        </CardBody>
        <hr width="100%"></hr>




        {/* <CardComment />   */}

        </Card>
      </div>
    )
  }
}



export default MainCard;
