import React, { Component } from 'react';
import { Form, FormGroup, Container, Input, Row, Card, Badge
} from 'reactstrap';
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

class  CreateArticle extends Component {
    render() {
        return (
            <div style ={{ width: '877px',
            margin:'auto',
                height: '1145px',
                borderRadius: '5px',                
                boxShadow: '0 4px 4px 0 rgba(0, 0, 0, 0.1)',
                backgroundColor: '#ffffff'}}>
                <div style={{marginTop:'50px'}}>
                <Form >
        <FormGroup>
          <Input type="email" name="email" id="exampleEmail" placeholder="Article Title" />
        </FormGroup>
        </Form>
                </div>

            <div className="App"  >
            <textarea cols="80" id="editor1" name="editor1" rows="10" data-sample-short>&lt;p&gt;This is some &lt;strong&gt;sample text&lt;/strong&gt;. You are using &lt;a href=&quot;https://ckeditor.com/&quot;&gt;CKEditor&lt;/a&gt;.&lt;/p&gt;</textarea>
  <script>
    CKEDITOR.replace('editor1', style={ {
      height: '260px',
      width: '700px',
    }});
  </script>
                <CKEditor 

                    editor={ ClassicEditor  } 
                    data ="<p> </p>"
                    onInit={ editor => {
                        // You can store the "editor" and use when it is needed.
                        console.log( 'Editor is ready to use!', editor );
                    } }
                    onChange={ ( event, editor ) => {
                        const data = editor.getData();
                        console.log( { event, editor, data } );
                    } }
                    onBlur={ editor => {
                        console.log( 'Blur.', editor );
                    } }
                    onFocus={ editor => {
                        console.log( 'Focus.', editor );
                    } }
                />
            </div>
            <Container>
            <Row>
                <Container style={{width:'877px', height:'625px', borderRadius:'5px',
                            boxShadow:' 0 4px 4px 0 rgba(0,0,0, 0.1)', backgroundColor:'#ffffff'
                }}>

                    <Card style={{width:'797px', height:'375px', backgroundColor:'#ebebeb'
                                   , marginTop:'30px' , marginBottom:'30px', marginLeft:'40px', marginRight:'40px',
                }}>
                    <h3 style={{margin:'auto', marginBottom:'21px', color:'#8c8c8c', fontSize:'18px' }}>
                        minimum size of 775x375px
                    </h3>
                    <Badge style={{margin:'auto', backgroundColor:'#2e4ffc', width:'171.4px', height:'45px',
                                    paddingTop:'10px', fontSize:'20px', color:'#ffffff', textAlign:'center', borderRadius:'99px'
                                    }}>
                        Upload Image
                    </Badge>

                    </Card>

                </Container>
            </Row>

        </Container>
            </div>
        );
    }
}
export default CreateArticle;
