import React, { Component } from 'react'
import { Card, CardImg, CardText, CardTitle} from 'reactstrap';
import AppFooter from '../AppFooter';
import AppNav from '../AppNav';
import axios from 'axios';

export default class Event extends Component {
  constructor(props){
    super(props);
    this.state = {
        detail: ''
    }
  }

  componentDidMount(){
      let detailId = this.props.match.params.prodId
      axios.get(`http://reduxblog.herokuapp.com/api/posts/${detailId}?key=barangku`)
      .then(res => {
          console.log('data detail',res.data)
          this.setState({
              detail : res.data
          })
      })
  }
  render() {
    console.log('ini adalah properti detail', this.state.detail)
    const {title, categories, content} = this.state.detail
    return (
      <div>
          <AppNav/>
          <h1>{title}</h1>
          <img src={categories} alt="" srcset=""/>
          <h2>{content}</h2>
      </div>
    )
    return (

      <div style={{backgroundColor:'#ececec'}}>
        <AppNav/>

     <div style={{marginTop:'60px', marginBottom:'60px',}}>

        <Card style={{backgroundColor:'#ffffff',width: '900px',margin:'auto'}} >
        <CardTitle style={{width: '900px',margin:'auto',
 marginTop:'40px',marginBottom:'20px', marginRight:'40px', marginLeft:'40px',
  fontFamily: 'Roboto',
  fontSize: '36px',
  fontWeight: '500',
  fontStretch: 'normal',
  color: '#2e2e2e' }}>CHOPPERLAND RE 350 VS CHOPPERLAND SR 400</CardTitle>


<CardImg  style={{ width: '776px',  height: '435.8px',marginTop:'20px', marginBottom:'20px', margin:'auto'}} src="
      https://scontent.fcgk8-2.fna.fbcdn.net/v/t1.0-9/37228699_10156511912949419_5427734426052198400_n.jpg?_nc_cat=106&_nc_eui2=AeFbW0UNjyUU6wUYr2IRN9OeP8hreReeKw5JhLn4zpkqOk1EoX7p-oyHW8DTrHt5IrPnY0tK6xlY0Q6fIY6iv9XgAzUKHS8I46Du69aZ0qCGvQ&_nc_ht=scontent.fcgk8-2.fna&oh=c180ed12f4d6e241827c9bed4879df13&oe=5D034823
      "  ></CardImg>

<CardText style={{width: '900px',height:'auto',
 marginTop:'40px', marginRight:'40px', marginLeft:'40px',
  fontFamily: 'Roboto',
   }}
> 

</CardText>

      </Card>

      </div>

      <AppFooter/>

      </div>
    )
  }
}
