import React, { Component } from 'react';
import { Form, FormGroup, Col, Container, Input, Row, Card, Button, TabPane,
} from 'reactstrap';
import PropTypes from 'prop-types';
// import classnames from 'classnames'
// import CKEditor from "react-ckeditor-component";
import axios from 'axios';
import CKEditor from 'ckeditor4-react';
import AppNav from '../AppNav';
import AppFooter from '../AppFooter';
import AddMedia from '../partbody/AddMedia';
import MainCard from '../partbody/MainCard'

class  CreateArticle extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      categories: '',
      products: []
    };
    this.handleChange = this.handleChange.bind( this );
    this.onEditorChange = this.onEditorChange.bind( this );

    this.updateData = this.updateData.bind(this)

  }

  handleTitleChange = event => {
    this.setState({ title: event.target.value })
}
handleSubmit = event => {
  event.preventDefault();
  axios.post('http://reduxblog.herokuapp.com/api/posts?key=barangku',
  {
      title: this.state.title,
      categories: this.state.categories
  }
  )
      .then(res => {
              alert('Artikel Berhasil Dibuat')
              this.updateData()
          }
      )
}
onEditorChange( evt ) {
  this.setState( {
      categories: evt.editor.getData()
  } );
}

handleChange( changeEvent ) {
  this.setState( {
      categories: changeEvent.target.value
  } );
}


updateData(){
  axios.get('http://reduxblog.herokuapp.com/api/posts?key=barangku')
  .then(res => {
      this.setState({
        products: res.data
      })
  });
}

componentDidMount(){
  axios.get('http://reduxblog.herokuapp.com/api/posts?key=barangku')
  .then(res => {
      this.setState({
        products: res.data
      })
  });
}


    render() {
      console.log('test', this.state.products)
       this.state.products.map((prod, index) => {
          return(
            <Col key={index}>
            <MainCard
              title={prod.title}
              color='primary'
            />
          </Col>
          )}
        );

      return(
            <div style ={{ backgroundColor:'#ececec'   }}>
<AppNav/>
<div style={{marginTop:'50px', marginBottom:'50px',}}>
<Container  >
        <Row class="row " tabId="1">
        <Col
       class="col-sm-4"

          >	<AddMedia/>

          </Col>
          <Col  class="col-sm-8" 
          > 
				 <div style ={{ width: '800px',  padding:'30px',height: '1145px', borderRadius: '5px', boxShadow: '0 4px 4px 0 rgba(0, 0, 0, 0.1)',
                backgroundColor: '#ffffff'}}>


                <Form   onSubmit={this.handleSubmit}   >
        <FormGroup>
          <Input type="text" name= 'this.state.title' id="title" placeholder="Article Title"  
          onChange={this.handleTitleChange} />
        </FormGroup>

        <CKEditor
                onclick="alert(CKEDITOR.instances.editor1.getData());"
                    categories={this.state.categories}
                    onChange={this.onEditorChange} />
                    <label>
                        Change val:
                        <textarea defaultValue={this.state.categories} onChange={this.handleChange} />
                    </label>
                    <EditorPreview categories={this.state.categories} />
        <Button color='primary' type="submit" > submit </Button>

        </Form>



              {/* <div className="App">

                <CKEditor 
              activeClass="p10" 
              content=""
              events={{
                "blur": this.onBlur,
                "afterPaste": this.afterPaste,
                "change": this.onChange
              }}
             />

            </div>  */}



            <Container>
            <Row>


                    <Card style={{width:'840px', height:'375px', backgroundColor:'#ebebeb'
                                   , marginTop:'30px' , marginBottom:'10px',
                }}>
                    <h3 style={{margin:'auto', marginBottom:'21px', color:'#8c8c8c', fontSize:'18px' }}>
                        minimum size of 775x375px
                    </h3>


                    <Button className="float-right"
            style={{width: '171.4px',
                height: '45px', margin:'auto', marginTop:'30px',
                borderRadius: '99px', paddingTop:'10px',
                backgroundColor: '#2e4ffc',
                fontFamily: 'Roboto',
                fontSize: '20px',
                fontWeight: '500',
                fontStyle: 'normal',
                fontStretch: 'normal',
                lineHeight: 'normal',
                letterSpacing: 'normal',
                color: '#ffffff'
            }}
            >Upload Image</Button>


                    </Card>

            </Row>

        </Container>

            </div>


        </Col>

        </Row>
      </Container>
            </div>
            <AppFooter/>
            </div>
        );
    }
}

class EditorPreview extends Component {
  render() {
      return (
          <div className="editor-preview">
              <h2>Rendered content:</h2>
              <div dangerouslySetInnerHTML={ { __html: this.props.categories } }></div>
          </div>
      );
  }
}

EditorPreview.defaultProps = {
  categories: ''
};

EditorPreview.propTypes = {
  categories: PropTypes.string
};


export default CreateArticle;
