import React, { Component } from 'react';
import { Form, FormGroup, Col, Container, Input, Row, Card, Button
} from 'reactstrap';
import CKEditor from "react-ckeditor-component";

import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { Link } from 'react-router-dom';
import AppNav from '../AppNav';
import AppFooter from '../AppFooter';
import AddMedia from '../partbody/AddMedia';

class  CreateArticle extends Component {
    constructor(props) {
        super(props);
        this.updateContent = this.updateContent.bind(this);
        this.state = {
            content: 'content',
        }
    }

    updateContent(newContent) {
        this.setState({
            content: newContent
        })
    }

    onChange(evt){
      console.log("onChange fired with event info: ", evt);
      var newContent = evt.editor.getData();
      this.setState({
        content: newContent
      })
    }

    onBlur(evt){
      console.log("onBlur event called with event info: ", evt);
    }

    afterPaste(evt){
      console.log("afterPaste event called with event info: ", evt);
    }

    render() {
        return (
            <div style ={{ backgroundColor:'#ececec'   }}>
<AppNav/>
<div style={{marginTop:'50px', marginBottom:'50px',}}>
<Container  >
        <Row class="row ">
        <Col
       class="col-sm-4"

          >	<AddMedia/>

          </Col>
          <Col  class="col-sm-8"
          > 
				 <div style ={{ width: '800px', 

            padding:'30px',
                height: '1145px',
                borderRadius: '5px',                
                boxShadow: '0 4px 4px 0 rgba(0, 0, 0, 0.1)',
                backgroundColor: '#ffffff'}}>


                <Form >
        <FormGroup>
          <Input type="email" name="email" id="exampleEmail" placeholder="Article Title" />
        </FormGroup>
        </Form>



                <div className="App">

                <CKEditor 
              activeClass="p10" 
              content={this.state.content} 
              events={{
                "blur": this.onBlur,
                "afterPaste": this.afterPaste,
                "change": this.onChange
              }}
             />

            </div>



            <Container>
            <Row>


                    <Card style={{width:'840px', height:'375px', backgroundColor:'#ebebeb'
                                   , marginTop:'30px' , marginBottom:'10px',
                }}>
                    <h3 style={{margin:'auto', marginBottom:'21px', color:'#8c8c8c', fontSize:'18px' }}>
                        minimum size of 775x375px
                    </h3>


                    <Button className="float-right"
            style={{width: '171.4px',
                height: '45px', margin:'auto', marginTop:'30px',
                borderRadius: '99px', paddingTop:'10px',
                backgroundColor: '#2e4ffc',
                fontFamily: 'Roboto',
                fontSize: '20px',
                fontWeight: '500',
                fontStyle: 'normal',
                fontStretch: 'normal',
                lineHeight: 'normal',
                letterSpacing: 'normal',
                color: '#ffffff'
            }}
            >Upload Image</Button>


                    </Card>

            </Row>

        </Container>

            </div>


        </Col>

        </Row>

      </Container>

            </div>
            <AppFooter/>
            </div>
        );
    }
}
export default CreateArticle;
