import React, { Component } from 'react';
import { Form, FormGroup, Container, Input, Row, Card, Button
} from 'reactstrap';
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { Link } from 'react-router-dom';
import AppNav from '../AppNav';
import AppFooter from '../AppFooter';

class  CreateArticle extends Component {
    render() {
        return (
            <div style ={{ backgroundColor:'#ececec',width: '900px',
            margin:'auto',
            padding:'30px',
                height: '1145px',
                borderRadius: '5px',                
                boxShadow: '0 4px 4px 0 rgba(0, 0, 0, 0.1)',
                backgroundColor: '#ffffff'}}>
<AppNav/>

                <Form >
        <FormGroup>
          <Input type="email" name="email" id="exampleEmail" placeholder="Article Title" />
        </FormGroup>
        </Form>



                <div className="App">

                <CKEditor

                    editor={ ClassicEditor }
                    data="<p> </p>"
                    onInit={ editor => {
                        // You can store the "editor" and use when it is needed.
                        console.log( 'Editor is ready to use!', editor );
                    } }
                    onChange={ ( event, editor ) => {
                        const data = editor.getData();
                        console.log( { event, editor, data } );
                    } }
                    onBlur={ editor => {
                        console.log( 'Blur.', editor );
                    } }
                    onFocus={ editor => {
                        console.log( 'Focus.', editor );
                    } }
                />

            </div>



            <Container>
            <Row>


                    <Card style={{width:'840px', height:'375px', backgroundColor:'#ebebeb'
                                   , marginTop:'30px' , marginBottom:'30px',
                }}>
                    <h3 style={{margin:'auto', marginBottom:'21px', color:'#8c8c8c', fontSize:'18px' }}>
                        minimum size of 775x375px
                    </h3>


                    <Button className="float-right"
            style={{width: '171.4px',
                height: '45px', margin:'auto', marginTop:'30px',
                borderRadius: '99px', paddingTop:'10px',
                backgroundColor: '#2e4ffc',
                fontFamily: 'Roboto',
                fontSize: '20px',
                fontWeight: '500',
                fontStyle: 'normal',
                fontStretch: 'normal',
                lineHeight: 'normal',
                letterSpacing: 'normal',
                color: '#ffffff'
            }}
            >Upload Image</Button>


                    </Card>

            </Row>

        </Container>
        <AppFooter/>
            </div>
        );
    }
}
export default CreateArticle;
