import axios from 'axios';

export default class Event extends Component {
  constructor(props){
    super(props);
    this.state = {
        detail: '',
        categories: '',
        products: []
    }
  }

  componentDidMount(){
      let detailId = this.props.match.params.prodId
      axios.get(`http://reduxblog.herokuapp.com/api/posts/${detailId}?key=barangku`)
      .then(res => {
          console.log('data detail',res.data)
          this.setState({
              detail : res.data
          })
      })
  }
