import React, { Component } from 'react';
import { Form} from 'reactstrap';
import PropTypes from 'prop-types';
import CKEditor from 'ckeditor4-react';
import { Button } from 'mdbreact';
import axios from 'axios';

class PageCkeEditor extends Component {
    constructor( props ) {
        super( props );

        this.state = {
            data: '<p>React is really <em>nice</em>!</p>',
            products: []
        };
        this.updateData = this.updateData.bind(this)
        this.handleChange = this.handleChange.bind( this );
        this.onEditorChange = this.onEditorChange.bind( this );
    }

    onEditorChange( evt ) {
        this.setState( {
            data: evt.editor.getData()
        } );
    }

    handleChange( changeEvent ) {
        this.setState( {
            data: changeEvent.target.value
        } );
    }

    handleSubmit = event => {
        event.preventDefault();
        axios.post('http://reduxblog.herokuapp.com/api/posts?key=barangku',
        {
            data: this.state.title
        }
        )
            .then(res => {
                    alert('article was added')
                    this.updateData()
                }
            )
      }

      updateData(){
        axios.get('http://reduxblog.herokuapp.com/api/posts?key=barangku')
        .then(res => {
            this.setState({
              products: res.data
            })
        });
      }
      componentDidMount(){
        axios.get('http://reduxblog.herokuapp.com/api/posts?key=barangku')
        .then(res => {
            this.setState({
              products: res.data
            })
        });
      }


      render() {
        return (
            <div>
                <Form   onSubmit={this.handleSubmit} >
                <CKEditor
                    data={this.state.title}
                    onChange={this.onEditorChange} />
                    <label>
                        Change val:
                        <textarea defaultValue={this.state.title} onChange={this.handleChange} />
                    </label>
                    <EditorPreview data={this.state.data} />
                    <Button color='primary' type="submit" >
                        Masuk Pak Eko
                    </Button>
                    </Form>
            </div>
        );
    }
}

class EditorPreview extends Component {

    render() {
        return (
            <div className="editor-preview">
                <h2>Rendered content:</h2>
                <div dangerouslySetInnerHTML={ { __html: this.props.data } }></div>
            </div>
        );
    }
}

EditorPreview.defaultProps = {
    data: ''
};

EditorPreview.propTypes = {
    data: PropTypes.string
};

export default PageCkeEditor;
