import React, { Component } from 'react';
import { Form, FormGroup, Col, Container, Input, Row, Card, Button, TabPane,
} from 'reactstrap';

import classnames from 'classnames'
import CKEditor from "react-ckeditor-component";
import axios from 'axios';
import AppNav from '../AppCarousel';
import AppFooter from '../AppFooter';
import AddMedia from '../partbody/AddMedia';
import MainCard from '../partbody/MainCard'

class  CreateArticle extends Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      title: ''

    };

    this.updateData = this.updateData.bind(this)
  }
    handleSubmit = event => {
        event.preventDefault();
        axios.post('http://reduxblog.herokuapp.com/api/posts?key=products2',
        {
            title: this.state.title,

        }

        )
            .then(res => {
                    alert('article was added')
                    this.updateData()
                    this.setState({ activeTab: '2' })
                }
            )
      }
   handleTitleChange = event => {
        this.setState({ title: event.target.value })
    }

  //   updateContent(newContent) {

  //   axios.get('http://reduxblog.herokuapp.com/api/posts?key=products2')
  //   .then(res => {
  //       this.setState({
  //         products: res.data
  //       })
  //   });
  // }
  updateData(){
    axios.get('http://reduxblog.herokuapp.com/api/posts?key=products2')
    .then(res => {
        this.setState({
          products: res.data
        })
    });
  }



    componentDidMount(){
        axios.get('http://reduxblog.herokuapp.com/api/posts?key=products2')
        .then(res => {
            this.setState({
              products: res.data
            })
        });
      }    

    // onChange(evt){
    //   console.log("onChange fired with event info: ", evt);
    //   var newContent = evt.editor.getData();
    //   this.setState({
    //     content: newContent
    //   })
    // }

    // onBlur(evt){
    //   console.log("onBlur event called with event info: ", evt);
    // }

    // afterPaste(evt){
    //   console.log("afterPaste event called with event info: ", evt);
    // }
    handleDelete = postId => {
      axios.delete(`http://reduxblog.herokuapp.com/api/posts/${postId}?key=products2`)
      .then(res => {
        console.log(res.data)
        alert('item removed')
        this.updateData()
      })
    }
    toggle(tab) {
      if (this.state.activeTab !== tab) {
        this.setState({
          activeTab: tab
        });
      }
    }
    render() {
      console.log('test', this.state.activeTab)
      const looping = this.state.products.map((prod, index) => {
          return(
            <Col key={index}>
            <MainCard
              title={prod.title}
              buttonName='delete'
              color='primary'
            />
          </Col>
          )}
        );
        return(
            <div style ={{ backgroundColor:'#ececec'   }}>
<AppNav/>
<div style={{marginTop:'50px', marginBottom:'50px',}}>
<Container  >
        <Row class="row ">
        <Col
       class="col-sm-4"

          >	<AddMedia/>

          </Col>
          <Col  class="col-sm-8" className={classnames({ active: this.state.activeTab === '1' })}
              onClick={() => { this.toggle('1'); }}
          > 
				 <div   className={classnames({ active: this.state.activeTab === '2' })}
              onClick={() => { this.toggle('2'); }} activeTab={this.state.activeTab}
              style ={{ width: '800px', 

            padding:'30px',
                height: '1145px',
                borderRadius: '5px',                
                boxShadow: '0 4px 4px 0 rgba(0, 0, 0, 0.1)',
                backgroundColor: '#ffffff'}}>



                <Form   activeTab={this.state.activeTab}   >
        <FormGroup onSubmit={this.handleSubmit}>
          <Input type="text" name= 'this.state.title' id="title" placeholder="Article Title"  
          onChange={this.handleTitleChange} />
        </FormGroup>
        </Form>



                {/* <div className="App">

                <CKEditor 
              activeClass="p10" 
              content={this.state.content} 
              events={{
                "blur": this.onBlur,
                "afterPaste": this.afterPaste,
                "change": this.onChange
              }}
             />

            </div> */}



            <Container>
            <Row>


                    <Card style={{width:'840px', height:'375px', backgroundColor:'#ebebeb'
                                   , marginTop:'30px' , marginBottom:'10px',
                }}>
                    <h3 style={{margin:'auto', marginBottom:'21px', color:'#8c8c8c', fontSize:'18px' }}>
                        minimum size of 775x375px
                    </h3>


                    <Button className="float-right"
            style={{width: '171.4px',
                height: '45px', margin:'auto', marginTop:'30px',
                borderRadius: '99px', paddingTop:'10px',
                backgroundColor: '#2e4ffc',
                fontFamily: 'Roboto',
                fontSize: '20px',
                fontWeight: '500',
                fontStyle: 'normal',
                fontStretch: 'normal',
                lineHeight: 'normal',
                letterSpacing: 'normal',
                color: '#ffffff'
            }}
            >Upload Image</Button>


                    </Card>

            </Row>

        </Container>

            </div>


        <Button color='primary' type="submit"> submit </Button>
        </Col>

        </Row>
        <TabPane tabId="2">
          <Row className='d-flex'>
            {looping}
          </Row>
          </TabPane>
      </Container>
            </div>
            <AppFooter/>
            </div>
        );
    }
}
export default CreateArticle;
