import React from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink, Row, Col, Container,  Form, FormGroup, Label, Input, Button } from 'reactstrap';
import AppCarousel from '../AppCarousel'
import classnames from 'classnames'
import MainCard from '../partbody/MainCard';
import axios from 'axios';

export default class Jauh extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      activeTab: '1',
      products: [],
      judulPost: '',
      categories: '',
      isiPost: '',
      pictures: []
    };

    this.updateData = this.updateData.bind(this)
  }

  handlejudulPostChange = event => {
      this.setState({ judulPost: event.target.value })
  }
  handleCategoriesChange = event => {
    this.setState({ categories: event.target.value })
  }
  handleisiPostChange = event => {
    this.setState({ isiPost: event.target.value })
  }

  handleSubmit = event => {
    event.preventDefault();
    axios.post('https://tempgaragelife.herokuapp.com/posts',
    {
        judulPost: this.state.judulPost,
        categories: this.state.categories,
        isiPost: this.state.isiPost
    }
    )
        .then(res => {
                alert('article was added')
                this.updateData()
                this.setState({ activeTab: '2' })
            }
        )
  }


  updateData(){
    axios.get('https://tempgaragelife.herokuapp.com/posts')
    .then(res => {
        this.setState({
          products: res.data
        })
    });
  }

  componentDidMount(){
    axios.get('https://tempgaragelife.herokuapp.com/posts')
    .then(res => {
        this.setState({
          products: res.data
        })
    });
  }

  handleDelete = postId => {
    axios.delete(`https://tempgaragelife.herokuapp.com/posts`)
    .then(res => {
      console.log(res.data)
      alert('item removed')
      this.updateData()
    })
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }
  render() {
    console.log('test', this.state.activeTab)
    const looping = this.state.products.map((prod, index) => {
        return(
          <Col md='4' key={index}>
          <MainCard
            image={prod.categories}
            judulPost={prod.judulPost}
            harga={prod.isiPost}
            deleteId= {(e) => this.handleDelete(prod.id)}
            buttonName='delete'
            color='primary'
          />
        </Col>
        )}
      );
    return (
      <div>
          <AppCarousel />
          <Container>
          <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === '1' })}
              onClick={() => { this.toggle('1'); }}
            >
              Add Data
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink  className={classnames({ active: this.state.activeTab === '2' })}
              onClick={() => { this.toggle('2'); }}

            >
              List Data
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={this.state.activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <Form onSubmit={this.handleSubmit}>
                        <FormGroup row>
                            <Label for="judulPost" sm={2} size="lg">judulPost</Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    placeholder="judulPost"
                                    bsSize="lg"
                                    onChange={this.handlejudulPostChange}
                                    name= 'this.state.judulPost'
                                />
                            </Col>
                            <Label for="Image" sm={2} size="lg">Image Link</Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    placeholder="Image Link"
                                    bsSize="lg"
                                    onChange={this.handleCategoriesChange}
                                    name='this.state.categpries'
                                />
                            </Col>
                            <Label for="Content" sm={2} size="lg">Content</Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    placeholder="Content"
                                    bsSize="lg"
                                    onChange={this.handleisiPostChange}
                                    name='this.state.content'
                                />
                            </Col>
                        </FormGroup>
                        <Button color='primary' type="submit"> submit </Button>
                </Form>
                <h1>upload images</h1>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
          <Row className='d-flex'>
            {looping}
          </Row>
          </TabPane>
        </TabContent>

          </Container>
      </div>
    );
  }
}
